mapper.select:
        SELECT


--         级联表的字段
        ,haixian.haixian_name as haixianName
        ,haixian.haixian_photo as haixianPhoto
        ,haixian.haixian_video as haixianVideo
        ,haixian.haixian_types as haixianTypes
        ,haixian.haixian_kucun_number as haixianKucunNumber
        ,haixian.haixian_old_money as haixianOldMoney
        ,haixian.haixian_new_money as haixianNewMoney
        ,haixian.haixian_clicknum as haixianClicknum
        ,haixian.shangxia_types as shangxiaTypes
        ,haixian.haixian_delete as haixianDelete
        ,haixian.haixian_content as haixianContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney

        FROM haixian_commentback  a
        left JOIN haixian haixian ON a.haixian_id = haixian.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.haixian_id = #{params.haixianId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and a.haixian_commentback_text like CONCAT('%',#{params.haixianCommentbackText},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                and a.reply_text like CONCAT('%',#{params.replyText},'%')


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




                and a.haixian_id IS NOT NULL


                and haixian.haixian_name like CONCAT('%',#{params.haixianName},'%')


                and haixian.haixian_video like CONCAT('%',#{params.haixianVideo},'%')


                and haixian.haixian_types = #{params.haixianTypes}



                  and haixian.haixian_kucun_number >= #{params.haixianKucunNumberStart}   


                  and haixian.haixian_kucun_number <= #{params.haixianKucunNumberEnd}   


                and haixian.haixian_kucun_number = #{params.haixianKucunNumber}


                  and haixian.haixian_old_money >= #{params.haixianOldMoneyStart}   


                  and haixian.haixian_old_money <= #{params.haixianOldMoneyEnd}   


                  and haixian.haixian_new_money >= #{params.haixianNewMoneyStart}   


                  and haixian.haixian_new_money <= #{params.haixianNewMoneyEnd}   


                  and haixian.haixian_clicknum >= #{params.haixianClicknumStart}   


                  and haixian.haixian_clicknum <= #{params.haixianClicknumEnd}   


                and haixian.haixian_clicknum = #{params.haixianClicknum}


                and haixian.shangxia_types = #{params.shangxiaTypes}



                  and haixian.haixian_delete >= #{params.haixianDeleteStart}   


                  and haixian.haixian_delete <= #{params.haixianDeleteEnd}   


                and haixian.haixian_delete = #{params.haixianDelete}


                and haixian.haixian_content like CONCAT('%',#{params.haixianContent},'%')



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   



        order by a.${params.orderBy} desc
